INSERT INTO `hf_article` VALUES (60, '明制‖汉服妆造', '‖一个端庄贵气的明制造型因为需要佩戴正凤以及华丽的首饰需要大而高的发髻衬托主凤发式也不宜过于复杂缭乱立太多的镂空的环反而会扰乱整体的视觉效果反而干净简单的底能更好的衬托首饰的精美', '血染迷恋', '1631203200', '/img/avatar/ooYBAGDBidSADulHAAB_ya_ZEnw622.jpg', '/img/article/60ddd790c9fa7460b30ca72c_1000-1333.jpg', '');
