INSERT INTO `hf_topic` VALUES (1, '2021国风大赏最美汉服集锦', '2021华裳九州国风大赏，海马体、十三余、重回汉唐等众多汉服品牌新品亮相，你觉得哪套汉服最美？知名主播李佳琦身着断桥残雪联名款汉服亮相活动现场，带货总值突破5000万。汉服文化和穿搭已在年轻人中形成一种新的“国潮”。');
INSERT INTO `hf_topic` VALUES (2, '风从东方来，舞出中国魂', '一袭汉服优雅起，万种意蕴乐中升！身穿汉服一舞动人，感受盛世中华。同袍们，一起跳舞吗？');
INSERT INTO `hf_topic` VALUES (3, '大揭秘！仙女这样梳头', '作为一名袍子，每一根头发丝都要精致！一套好看的汉服怎么能少了合适的发型搭配？快来get精美又简单的汉服发型！你学fei了吗？');
INSERT INTO `hf_topic` VALUES (4, '同袍云雅集', ' 在这里，你可以通过发布视频的方式，与同袍们在云端交流绘画、穿搭、妆容vlog、科普、摄影等方面的心得或分享圈内资讯信息。只要稿件具有吸引力且与国风、汉服相关，还可能获得丰厚大奖哦~加入我们的官方群聊：494293437，了解更多详细信息！');
INSERT INTO `hf_topic` VALUES (5, '图图表情包模仿大赛', '大家好！我是住在翻斗花园的胡图图！听说网上的哥哥姐姐们都在用我的表情包，我有一个好消息告诉大家喔：图图表情包模仿大赛开始啦！快来比比谁更像我吧！');
INSERT INTO `hf_topic` VALUES (6, '萌宠也能驾驭汉服？！', '国潮再兴，同袍们不但自己穿上汉服，还不忘给家里的主子安排上。交领右衽和褒衣广袖的汉服同宠物的跨界结合会碰撞出什么样的火花呢？铲屎官有的我也要有，安排！');
INSERT INTO `hf_topic` VALUES (7, '国风妆容能有多绝？', '国风妆容对同袍们来说就是yyds！精致的国风妆容不仅超级吸睛，还能为汉服造型锦上添花！快来分享那些超惊艳的国风妆容吧');
INSERT INTO `hf_topic` VALUES (8, '乞巧节·这手艺惊艳所有人', '七夕有奖活动七夕节又称乞巧节，一直有着向七仙女“乞巧”（祈求巧手）的民俗，在这个特别的节日里，一起来领略那些令人惊艳的手艺吧！你学fei了吗？');
INSERT INTO `hf_topic` VALUES (9, '新学期新汉服，你准备好了吗？', '新学期新气象，各位同学们即将重返校园啦~穿汉服迎接新学期是一种什么体验？同袍们一定充满期待、跃跃欲试！快分享你的新学期汉服穿搭吧！');
INSERT INTO `hf_topic` VALUES (10, '汉服最佳打卡地', '出片率超高的汉服打卡天堂，穿着汉服往那一站就很绝！试问你的宝藏打卡地上榜了嘛？');
